import { Navbar,Nav,Form,Button,FormControl,Card,Container,Row,Col,DropdownButton,Dropdown,Select,Option,Jumbotron,InputGroup} from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import Headbar from './headbar';
import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import Bottom from './bottom';

const Edit = () => {
    
  const {id} = useParams();
  const [station, setStation] = useState([])

  useEffect(() => {
    fetch('/station')
      .then(res => res.json())
      .then(station => setStation(station))
      console.log(station)
  },[])

  const [product,setProduct] = useState([])

  useEffect(() => {
    fetch('/products/'+id)
    .then(res => res.json())
    .then(product => setProduct(product))
    console.log(product)
  },[])

  const [name, setname] = useState('hhh');
  const [status, setstatus] = useState('');
  const [station1, setstation1] = useState('');
  const [station2, setstation2] = useState('');
  const [fibertype, setfibertype] = useState('');
  const [distance, setdistance] = useState('');
  const [core, setcore] = useState('');
  const [use_core, setuse_core] = useState('');
  const [notuse_core, setnotuse_core] = useState('');
  const [date, setdate] = useState('');
  const [person, setperson] = useState('');
  const [anyelse, setanyelse] = useState('');
  

  const handleSubmit = () => {
    if(window.confirm('are  you sure?')){
    
    const blog = { name,status,station1,station2,fibertype,distance,
        core,use_core,notuse_core,date,person,anyelse,
    }
    fetch('/products/'+id, {
      method: 'PUT',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    }).then(() => {
      console.log('new blog added');
    })
  }
  }

  return (
    <div className="App-main">
    <Headbar></Headbar>
    <br></br>
    <Container>
          <Row>
          <Col></Col>
          <Col xs="12">
            <Card>
            <Card.Header as="h5">Edit</Card.Header>
            <Card.Body>
            <form>
                {/*name*/}
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>รายการ</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="string" 
                  required 
                  value={product.name}
                  onTimeUpdate={(e) => setProduct.name(e.target.value)}
                  se
                >
                </FormControl>
                </InputGroup>

                {/*status+type+insertplace*/}
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>สถานะการใช้งาน</InputGroup.Text>
                </InputGroup.Prepend>
                <Form.Control 
                  required
                  type="string" 
                  defaultValue={product.status} 
                  
                  as="select"
                  onSubmit={(e) => setProduct.status(e.target.value)}
                  
                >
                    <option>เลือกรายการ</option>
                    <option>กำลังใช้งาน</option>
                    <option>อยู่ระหว่างการดำเนินการ</option>
                    <option>ยังไม่ดำเนินการ</option>
                </Form.Control>
                <InputGroup.Prepend>
                <InputGroup.Text>ประเภทของสาย</InputGroup.Text>
                </InputGroup.Prepend>
                <Form.Control 
                  type="string" 
                  required 
                  value={product.fibertype}
                  onChange={(e) => setProduct.fibertype(e.target.value)}
                  as="select"
                  >
                    <option>เลือกรายการ</option>
                    <option>Single Mode</option>
                    <option>Multi Mode</option>
                    <option>ไม่ระบุ</option>
                </Form.Control>
                <Button variant="success" type="submit" href="/addstation">
                เพิ่มตัวเลือกสถานที่ต้นทางหรือปลายทางเพิ่มเติม
                </Button>
                </InputGroup>

                {/*s1+s2*/}
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>ต้นทาง</InputGroup.Text>
                </InputGroup.Prepend>
                <Form.Control 
                  type="string" 
                  required 
                  value={product.station1}
                  onChange={(e) => setProduct.station1(e.target.value)}
                  as="select"
                  >
                    <option>เลือกรายการ</option>
                    {station.map( el=> (
                    <option key={el.id}>
                      {el.name}
                    </option>
                    ))}
                </Form.Control>
                <InputGroup.Prepend>
                <InputGroup.Text>ปลายทาง</InputGroup.Text>
                </InputGroup.Prepend>
                <Form.Control 
                  type="string" 
                  required 
                  value={product.station2}
                  onChange={(e) => setProduct.station2(e.target.value)}
                  as="select"
                  placeholder="Password"
                  >
                    <option>เลือกรายการ</option>
                    {station.map( el=> (
                    <option key={el.id}>
                      {el.name}
                    </option>
                    ))}
                </Form.Control>
                </InputGroup>
                
                {/*core+distance*/}
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>ระยะทางโดยประมาณ(m)</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="number" 
                  required 
                  value={product.distance}
                  onChange={(e) => setProduct.distance(e.target.value)}
                />
                <InputGroup.Prepend>
                <InputGroup.Text>จำนวน Core</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="number" 
                  required 
                  value={product.core}
                  onChange={(e) => setProduct.core(e.target.value)}
                />
                <InputGroup.Prepend>
                <InputGroup.Text>ถูกใช้งาน</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="number" 
                  required 
                  value={product.use_core}
                  onChange={(e) => setProduct.use_core(e.target.value)}
                />
                <InputGroup.Prepend>
                <InputGroup.Text>ยังไม่ได้ใช้งาน</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="number" 
                  required 
                  value={product.notuse_core}
                  onChange={(e) => setProduct.notuse_core(e.target.value)}
                />

                </InputGroup>

                {/*date+restname*/}
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>วันที่ดำเนินงาน</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="string" 
                  required 
                  value={product.date}
                  onChange={(e) => setProduct.date(e.target.value)}
                />
                <InputGroup.Prepend>
                <InputGroup.Text>ผู้ดำเนินงาน</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="string" 
                  required 
                  value={product.person}
                  onChange={(e) => setProduct.person(e.target.value)}
                />
                </InputGroup>

                
                {/*repair
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>ประวัติการซ่อม</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  as="textarea" 
                  aria-label="With textarea" 
                  type="string" 
                  required 
                  value={product.repair}
                  onChange={(e) => setProduct.repair(e.target.value)}
                />
                </InputGroup>*/}

                {/*else*/}
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>ข้อมูลเพิ่มเติม</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  as="textarea" 
                  aria-label="With textarea" 
                  type="string" 
                  required 
                  value={product.anyelse}
                  onChange={(e) => setProduct.anyelse(e.target.value)}
                />
                </InputGroup>
                <Button type="submit" onClick={handleSubmit} href="/list">เพิ่มรายการ</Button>
            </form>
            
            <br></br>
          
            </Card.Body>
            </Card>
          </Col>
          <Col></Col>
          </Row>

    </Container>
    <Bottom></Bottom>
    </div>
  );
}

export default Edit;
