import React, { useState, useEffect } from "react";
import UploadService from "./FileUploadService";
import { InputGroup,Accordion,CardGroup,Carousel,ListGroup,Table,Badge,Navbar,Nav,Form,Button,FormControl,Card,Container,Row,Col,DropdownButton,Dropdown,Select,Option,Jumbotron} from 'react-bootstrap';

const UploadFiles = () => {

    const [selectedFiles, setSelectedFiles] = useState(undefined);
    const [currentFile, setCurrentFile] = useState(undefined);
    const [progress, setProgress] = useState(0);
    const [message, setMessage] = useState("");
  
    const [fileInfos, setFileInfos] = useState([]);

    const selectFile = (event) => {
        setSelectedFiles(event.target.files);
      };

    useEffect(() => {
        UploadService.getFiles().then((response) => {
          setFileInfos(response.data);
        });
      }, []);

    
    const [imgid, setimgid] = useState('');

    const handleSubmit = () => {
      if(window.confirm('are  you sure?')){
       
        const blog = {imgid}
    
        fetch('/products', {
          method: 'POST',
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(blog)
        }).then(() => {
          console.log('new blog added');
        })
      }

      
    }
    
    
  
    return (
        <div>
      {currentFile && (
        <div className="progress">
          <div
            className="progress-bar progress-bar-info progress-bar-striped"
            role="progressbar"
            aria-valuenow={progress}
            aria-valuemin="0"
            aria-valuemax="100"
            style={{ width: progress + "%" }}
          >
            {progress}%
          </div>
        </div>
      )}
        <form>
        <label className="btn btn-default">

        <InputGroup size="sm" className="mb-3">
            <InputGroup.Prepend size="sm">
            <InputGroup.Text size="sm">file</InputGroup.Text>
            </InputGroup.Prepend >
            <FormControl 
            type="file" 
            required 
            //value={rid}
            size="sm"
            onChange={selectFile}
            />
        </InputGroup> 
        
        <InputGroup size="sm" className="mb-3">
            <InputGroup.Prepend size="sm">
            <InputGroup.Text size="sm">id</InputGroup.Text>
            </InputGroup.Prepend >
            <FormControl 
            type="string" 
            required 
            value={imgid}
            size="sm"
            onChange={(e) => setimgid(e.target.value)}
            />
        </InputGroup>                

        </label>
        </form>

      <button
        className="btn btn-success"
        disabled={!selectedFiles}
        onClick={upload}
      >
        Upload
      </button>

      <div className="alert alert-light" role="alert">
        {message}
      </div>

      <div className="card">
        <div className="card-header">List of Files</div>
        <ul className="list-group list-group-flush">
          {fileInfos &&
            fileInfos.map((file, index) => (
              <li className="list-group-item" key={index}>
                <a href={file.url}>{file.name}</a>
              </li>
            ))}
        </ul>
      </div>
    </div>
    );
};

export default UploadFiles;
