import {Toast,ListGroup,Modal,Navbar,Nav,Form,Button,FormControl,Card,Container,Row,Col,DropdownButton,Dropdown,Select,Option,Jumbotron,InputGroup} from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import Headbar from './headbar';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router-dom";
import { Link } from 'react-router-dom';

const Editstation = () => {
  

  const {id} = useParams();
  
  const [mystation, setmyStation] = useState([])
  useEffect(() => {
    fetch('/station/'+id)
      .then(res => res.json())
      .then(mystation => setmyStation(station))
      console.log(mystation)
  },[])
  
  const [name,setname] = useState(station.name);
  const handleSubmit=()=> {
    if(window.confirm('are  you sure?')){
      const blog = {name}
      fetch('/station/'+id, {
        method: 'PATCH',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(blog)
      }).then(() => {
        console.log('new blog added');
      })
    }
    
  }

  const [station, setStation] = useState([])
  useEffect(() => {
    fetch('/station')
      .then(res => res.json())
      .then(station => setStation(station))
      console.log(station)
  },[])

  const Del = (dataid) =>
  {
    if(window.confirm('are  you sure?'))
    {
      fetch('/station/'+dataid, 
      { method: 'DELETE' ,
        header: {'Accept':'application/json','Content-Type':'application/json'}
      })
    }
  }
 

  return (
      <div>
            <Container>
            <Row>
            <Col></Col>
            <Col xs="10">
                  <br></br>
                  <ListGroup  as="ul" >
                  <div>{station.map( el=> (
                  <span key={el.id}>
                  <ListGroup.Item  as="ul">
                  <Row>
                  <Col xs="10"><Icon.GeoAltFill/>{" "}{el.name}</Col>
                  <Col xs="2">
                    <Button variant="danger" type="submit" size="sm" onClick={()=>Del(el._id)} href="/addstation">
                      <Icon.Trash/>
                    </Button>{' '}
                    <Link  onClick = {()=>ed(el._id)}>
                        <Button type="submit" variant="warning"  size="sm" >
                        <Icon.PencilSquare/>
                        </Button>
                    </Link>
                  </Col>
                  </Row>
                  </ListGroup.Item>
                  
                  </span>
                  ))}
                  </div>
                  </ListGroup>
            </Col>
            <Col></Col>
            </Row>
            </Container>
      </div>
    );
}

const Editstationfeed = () => {
  
  const [name, setName] = useState('');
  const handleSubmit = () => {
    if(window.confirm('are  you sure?')){
   
    const blog = { name };

    fetch('/station', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    }).then(() => {
      console.log('new blog added');
    })
  }
  }

  return (
    <div className="App-main">
    <Headbar></Headbar>
    <br></br>
    <Container>
          <Row>
          <Col></Col>
          <Col xs="12">
            <Card>
            <Card.Header as="h5">เพิ่มสถานที่</Card.Header>
            <Card.Body>
                
                <form>
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>station</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="string" 
                  required 
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <Button type="submit" onClick={handleSubmit} href="/addstation">เพิ่ม</Button>
                
                </InputGroup>

                </form>

            </Card.Body>
            </Card>
          </Col>
          <Col></Col>
          </Row>

    <Editstation></Editstation>
    </Container>
    </div>
  );
}

export default Editstationfeed;
