import {Toast,ListGroup,Modal,Navbar,Nav,Form,Button,FormControl,Card,Container,Row,Col,DropdownButton,Dropdown,Select,Option,Jumbotron,InputGroup} from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import Headbar from './headbar';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router-dom";

function Example() {
  const [showA, setShowA] = useState(false);
  const toggleShowA = () => setShowA(!showA);
  

  return (
        <div>
        <Row>
      
        
        <Col xs="1"><Toast show={showA} onClose={toggleShowA}>
          <Toast.Header>
            <img
              src="holder.js/20x20?text=%20"
              className="rounded mr-2"
              alt=""
            />
            <strong className="mr-auto">Bootstrap</strong>
            <small>11 mins ago</small>
          </Toast.Header>
          <Toast.Body>Woohoo, you're reading this text in a Toast!</Toast.Body>
        </Toast>
        
        </Col>
        </Row>
        </div>

  );
}

const Filldata = () => {
  const [station, setStation] = useState([])
  
  useEffect(() => {
    fetch('/station')
      .then(res => res.json())
      .then(station => setStation(station))
      console.log(station)
  },[])

  const Del = (dataid) =>
  {
    if(window.confirm('are  you sure?'))
    {
      fetch('/station/'+dataid, 
      { method: 'DELETE' ,
        header: {'Accept':'application/json','Content-Type':'application/json'}
      })
    }
  }

  const handleSubmit = () => {
    if(window.confirm('are  you sure?')){
    const blog = {}
    fetch('/products/', {
      method: 'PATCH',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    }).then(() => {
      console.log('new blog added');
    })
  }
  }
  
  function hhh(name,id) {
      return (
      <div>
      <Row>
      
      <Col xs="10"><Icon.GeoAltFill/>{" "}{name}</Col>
      <Col xs="2">
      <Button variant="danger" type="submit" size="sm" onClick={()=>Del(id)} href="/addstation"><Icon.Trash/></Button>{' '}<Example></Example>
      </Col>
  
      </Row>
      </div>
      );
    
    
  }

  return (
      <div>
            <Container>
            <Row>
            <Col></Col>
            <Col xs="12">
                  <br></br>
                  
                  <ListGroup  as="ul" >
                  <ul>{station.map( el=> (
                  <span key={el.id}>
                  
                  <ListGroup.Item  as="ul">
                  {hhh(el.name,el._id)}
                  </ListGroup.Item>
                  
              
                  </span>
                  ))}
                  
                  
                  </ul>
                  </ListGroup>
            </Col>
            <Col></Col>
            </Row>
            </Container>
      </div>
    );
}

const Addstation = () => {
  
  const [name, setName] = useState('');
  const handleSubmit = () => {
    if(window.confirm('are  you sure?')){
   
    const blog = { name };

    fetch('/station', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    }).then(() => {
      console.log('new blog added');
    })
  }
  }

  return (
    <div className="App-main">
    <Headbar></Headbar>
    <br></br>
    <Container>
          <Row>
          <Col></Col>
          <Col xs="12">
            <Card>
            <Card.Header as="h5">เพิ่มสถานที่</Card.Header>
            <Card.Body>
                
                <form>
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>station</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="string" 
                  required 
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <Button type="submit" onClick={handleSubmit} href="/addstation">เพิ่ม</Button>
                
                </InputGroup>

                </form>

            </Card.Body>
            </Card>
          </Col>
          <Col></Col>
          </Row>

    <Filldata></Filldata>
    </Container>
    </div>
  );
}

export default Addstation;
