import { Navbar,Nav,Form,Button,FormControl,Card,Container,Row,Col,DropdownButton,Dropdown,Select,Option,Jumbotron,InputGroup} from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import Headbar from './headbar';
import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import Bottom from './bottom';

const Edit = () => {
  const {id} = useParams();
  const [station,setStation] = useState([])
  const [product,setProduct] = useState([])
  const [name,setname] = useState('');
  const [status,setstatus] = useState('');
  const [station1,setstation1] = useState('');
  const [station2,setstation2] = useState('');
  const [fibertype,setfibertype] = useState('');
  const [distance,setdistance] = useState('');
  const [core,setcore] = useState('');
  const [use_core,setuse_core] = useState('');
  const [notuse_core,setnotuse_core] = useState('');
  const [date,setdate] = useState('');
  const [person,setperson] = useState('');
  const [anyelse,setanyelse] = useState('');

  useEffect(() => {
    getStation();
    getUsers();
  }, [])

  const getStation = () => {
    fetch('/station')
      .then(res => res.json())
      .then(station => setStation(station))
      console.log(station)
  }

  const getUsers = () =>{
    fetch('/products/'+id)
    .then((res) => {
      res.json()
      .then((res) => {
        setProduct(res);
        setname(res[0].name)
        setstatus(res[0].status)
        setstation1(res[0].station1)
        setstation2(res[0].station2)
        setfibertype(res[0].fibertype)
        setdate(res[0].date)
        setdistance(res[0].distance)
        setcore(res[0].core)
        setuse_core(res[0].use_core)
        setnotuse_core(res[0].notuse_core)
        setperson(res[0].person)
        setanyelse(res[0].anyelse)
      })
    })
  }

  /*const updateUser = () =>{
    const item = {name,status,station1,station2,fibertype,distance,
      core,use_core,notuse_core,date,person,anyelse,
    }
    console.warn("item",item)
    fetch('/products/'+id, {
      method: 'PUT',
      headers:{
        'Accept':'application/json',
