import React, { useState, useEffect } from "react";
import UploadService from "./FileUploadService";
import { InputGroup,Accordion,CardGroup,Carousel,ListGroup,Table,Badge,Navbar,Nav,Form,Button,FormControl,Card,Container,Row,Col,DropdownButton,Dropdown,Select,Option,Jumbotron} from 'react-bootstrap';

const UploadFiles = () => {

    const [selectedFiles, setSelectedFiles] = useState(undefined);
    const [currentFile, setCurrentFile] = useState(undefined);
    const [progress, setProgress] = useState(0);
    const [message, setMessage] = useState("");
  
    const [fileInfos, setFileInfos] = useState([]);

    const selectFile = (event) => {
        setSelectedFiles(event.target.files);
      };

    useEffect(() => {
        UploadService.getFiles().then((response) => {
          setFileInfos(response.data);
        });
      }, []);

    
    //const [imgid, setimgid] = useState('');

    const upload = () => {
        let currentFile = selectedFiles[0];
    
        setProgress(0);
        setCurrentFile(currentFile);
    
        UploadService.upload(currentFile, (event) => {
          setProgress(Math.round((100 * event.loaded) / event.total));
        })
          .then((response) => {
            setMessage(response.data.message);
            return UploadService.getFiles();
          })
          .then((files) => {
            setFileInfos(files.data);
          })
          .catch(() => {
            setProgress(0);
            setMessage("Could not upload the file!");
            setCurrentFile(undefined);
          });
    
        setSelectedFiles(undefined);
    }
    
    
  
    return (
        <div>

        <form action="local/upload" method="POST" enctype= "multipart/form-data">
        <input  type="text" 
                name="fname"
                ></input>
        <input  type="file" 
                onChange={this.selectFile} 
                //name="pic"
        ></input>
        <Button  
                //type = "submit" 
                disabled={!selectedFiles}
                onClick={this.upload}>
        up
        </Button>
        </form>
      
      

      <div className="alert alert-light" role="alert">
        {message}
      </div>

      <div className="card">
        <div className="card-header">List of Files</div>
        <ul className="list-group list-group-flush">
          {fileInfos &&
            fileInfos.map((file, index) => (
              <li className="list-group-item" key={index}>
                <a href={file.url}>{file.name}</a>
              </li>
            ))}
        </ul>
      </div>
    </div>
    );
};

export default UploadFiles;
