import {ListGroup,Modal,Navbar,Nav,Form,Button,FormControl,Card,Container,Row,Col,DropdownButton,Dropdown,Select,Option,Jumbotron,InputGroup} from 'react-bootstrap';
import * as Icon from 'react-bootstrap-icons';
import Headbar from './headbar';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router-dom";


const Filldata = () => {
  const [station, setStation] = useState([])
  
  useEffect(() => {
    fetch('/station')
      .then(res => res.json())
      .then(station => setStation(station))
      console.log(station)
  },[])

  const Del = (dataid) =>
  {
    if(window.confirm('are  you sure?'))
    {
      fetch('/station/'+dataid, 
      { method: 'DELETE' ,
        header: {'Accept':'application/json','Content-Type':'application/json'}
      })
    }
  }

  var c = 1;

  function hhh(name,id,c) {
    c=c+1;

    if(c==1) {             
    return (
    <div>
    <Row>
    
    <Col xs="11">{c}{"   Location : "}{name}</Col>
    <Col xs="1">
    <Button variant="danger" type="submit" size="sm" onClick={()=>Del(id)} href="/addstation"><Icon.Trash/></Button>
    </Col>

    </Row>
    </div>
    );
    }
    else {             
      return (
      <div>
      <Row>
      
      <Col xs="11">{"   Location2 : "}{name}</Col>
      <Col xs="1">
      <Button variant="danger" type="submit" size="sm" onClick={()=>Del(id)} href="/addstation"><Icon.Trash/></Button>
      </Col>
  
      </Row>
      </div>
      );
      }

  }

  return (
      <div>
            <Container>
            <Row>
            <Col></Col>
            <Col xs="12">
                  <br></br>
                  
                  <ListGroup  as="ul" >
                  <ul>{station.map( el=> (
                  <span key={el.id}>
                  
                  <ListGroup.Item  as="ul">
                  {hhh(el.name,el._id,c)}
                  </ListGroup.Item>
                  
              
                  </span>
                  ))}
                  
                  
                  </ul>
                  </ListGroup>
            </Col>
            <Col></Col>
            </Row>
            </Container>
      </div>
    );
}

const Addstation = () => {
  
  const [name, setName] = useState('');
  const handleSubmit = () => {
    if(window.confirm('are  you sure?')){
   
    const blog = { name };

    fetch('/station', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(blog)
    }).then(() => {
      console.log('new blog added');
    })
  }
  }

  return (
    <div className="App-main">
    <Headbar></Headbar>
    <br></br>
    <Container>
          <Row>
          <Col></Col>
          <Col xs="12">
            <Card>
            <Card.Header as="h5">เพิ่มสถานที่</Card.Header>
            <Card.Body>
                
                <form>
                <InputGroup className="mb-3">
                <InputGroup.Prepend>
                <InputGroup.Text>station</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl 
                  type="string" 
                  required 
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
                <Button type="submit" onClick={handleSubmit} href="/addstation">เพิ่ม</Button>
                </InputGroup>

                </form>

            </Card.Body>
            </Card>
          </Col>
          <Col></Col>
          </Row>

    <Filldata></Filldata>
    </Container>
    </div>
  );
}

export default Addstation;
